"use strict";exports.id=448,exports.ids=[448],exports.modules={2448:t=>{t.exports=JSON.parse('{"services":{"title":"خدماتي","subtitle":"خدمات احترافية لمساعدة عملك على النمو","intro":{"title":"ما أقدمه","description":"أقدم خدمات متخصصة في إدارة المنتجات والتسويق الرقمي لمساعدة الشركات على بناء منتجات رقمية ناجحة والوصول إلى جمهورها المستهدف بفعالية."},"servicesList":[{"title":"استراتيجية المنتج","description":"تطوير استراتيجيات شاملة للمنتج تتماشى مع أهداف عملك واحتياجات المستخدمين. أساعدك في تحديد رؤية منتجك، وتحديد فرص السوق، وإنشاء خارطة طريق للنجاح.","features":["تحليل السوق والمنافسين","تطوير رؤية وخارطة طريق المنتج","أطر تحديد أولويات الميزات","استراتيجية الدخول إلى السوق","مقاييس ومؤشرات أداء المنتج"],"icon":"strategy"},{"title":"التسويق الرقمي","description":"إنشاء وتنفيذ استراتيجيات تسويق رقمي قائمة على البيانات لزيادة الوعي بالعلامة التجارية، وتوليد العملاء المحتملين، وزيادة التحويلات. أساعدك في الوصول إلى جمهورك المستهدف من خلال القنوات المناسبة.","features":["تطوير استراتيجية التسويق","تخطيط تسويق المحتوى","حملات تحسين محركات البحث والتسويق عبرها","التسويق عبر وسائل التواصل الاجتماعي","أتمتة التسويق عبر البريد الإلكتروني"],"icon":"marketing"},{"title":"تطوير المنتجات","description":"توجيه عملية تطوير منتجك من المفهوم إلى الإطلاق. أساعدك في تنفيذ منهجيات أجايل، وإدارة الفرق متعددة التخصصات، وضمان تلبية منتجك لتوقعات المستخدمين.","features":["تنفيذ أجايل","إنشاء قصص المستخدم","تخطيط وتنفيذ السبرنت","اختبار المنتج وضمان الجودة","تخطيط الإطلاق"],"icon":"development"},{"title":"أبحاث المستخدمين","description":"فهم احتياجات وسلوكيات ونقاط ألم المستخدمين من خلال بحث شامل. أساعدك في جمع الرؤى التي تؤثر على قرارات المنتج وتخلق تجارب مستخدم أفضل.","features":["مقابلات واستطلاعات المستخدمين","اختبار قابلية الاستخدام","رسم خرائط رحلة العملاء","تطوير الشخصيات","تحليل المنافسين"],"icon":"research"},{"title":"استراتيجيات النمو السريع","description":"تنفيذ استراتيجيات مبتكرة لتسريع نمو منتجك. أساعدك في تحديد فرص الاستحواذ السريع على المستخدمين، والتفعيل، والاحتفاظ، وتوليد الإيرادات.","features":["أطر تجارب النمو","استراتيجيات اختبار A/B","قنوات اكتساب المستخدمين","تحسين الاحتفاظ","تحسين معدل التحويل"],"icon":"growth"},{"title":"تحليلات المنتج","description":"إعداد وتحليل مقاييس المنتج لاتخاذ قرارات مبنية على البيانات. أساعدك في تتبع مؤشرات الأداء الرئيسية المناسبة، وتفسير بيانات سلوك المستخدم، وتحديد فرص التحسين.","features":["تنفيذ التحليلات","إنشاء لوحات المعلومات","تحليل سلوك المستخدم","تحسين مسار التحويل","تقارير الرؤى القابلة للتنفيذ"],"icon":"analytics"}],"process":{"title":"منهجية عملي","steps":[{"title":"الاكتشاف","description":"أبدأ بفهم أهداف عملك، والجمهور المستهدف، والتحديات الحالية من خلال مناقشات وأبحاث متعمقة."},{"title":"الاستراتيجية","description":"بناءً على رؤى الاكتشاف، أطور استراتيجية مخصصة تلبي احتياجاتك وأهدافك المحددة."},{"title":"التنفيذ","description":"أعمل عن كثب مع فريقك لتنفيذ الاستراتيجية، وتقديم التوجيه والدعم طوال العملية."},{"title":"القياس","description":"أتتبع المقاييس الرئيسية لقياس فعالية عملنا وإجراء تعديلات مبنية على البيانات حسب الحاجة."},{"title":"التحسين","description":"أقوم بتحسين الاستراتيجية والتنفيذ باستمرار بناءً على بيانات الأداء وتغير ظروف السوق."}]},"testimonials":{"title":"آراء العملاء","items":[{"quote":"العمل مع هذا المستشار غيّر استراتيجية منتجنا. ساعدتنا رؤاه في تحديد أولويات الميزات المناسبة والوصول إلى السوق المستهدف بفعالية.","author":"سارة جونسون","position":"مديرة المنتج، تك كورب"},{"quote":"أدت استراتيجية التسويق الرقمي التي تم تطويرها لشركتنا الناشئة إلى زيادة بنسبة 200% في العملاء المؤهلين خلال ثلاثة أشهر فقط.","author":"مايكل تشن","position":"الرئيس التنفيذي، جروث ستارت"},{"quote":"ساعدتنا خبرتهم في إدارة المنتجات على تبسيط عملية التطوير لدينا وإطلاق منتجنا قبل الموعد المحدد.","author":"إيميلي رودريغيز","position":"المديرة التقنية، إنوفيت تك"}]},"cta":{"title":"هل أنت مستعد للعمل معًا؟","description":"دعنا نناقش كيف يمكنني مساعدة عملك على النمو من خلال حلول مخصصة لإدارة المنتجات والتسويق الرقمي.","button":"تواصل معي"}}}')}};